import React, { useState, Component } from 'react';
import { render } from 'react-dom';
import _ from 'lodash';
import './index.css';
import 'semantic-ui-css/semantic.min.css';
import {
  Button,
  Card,
  CardContent,
  CardDescription,
  CardGroup,
  CardHeader,
  CardMeta,
  Divider,
  Image,
  Placeholder,
  PlaceholderHeader,
  PlaceholderImage,
  PlaceholderLine,
  PlaceholderParagraph,
} from 'semantic-ui-react';

const cards = [
  {
    avatar: 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/NPP_Symbol.png/100px-NPP_Symbol.png',
    header: "National People's Power",
    header2: "ජාතික ජන බලවේගය | தேசிய மக்கள் சக்தி",
    
  },
  {
    avatar: '/images/avatar/large/matthew.png',
    header: "Samagi Jana Balawegaya | සමගි ජනබලවේගය | ஐக்கிய மக்கள் சக்தி",
  },
  {
    avatar: '/images/avatar/large/molly.png',
    header: "National People's Power | ජාතික ජන බලවේගය | தேசிய மக்கள் சக்தி",
  },
]

const VotingUI = () => {
  const [loading, setLoading] = useState(false);
  const handleLoadingClick = () => {
    setLoading(true);

    setTimeout(() => {
      setLoading(false);
    }, 3000)
  }

  return <>
    <Button loading={loading} onClick={handleLoadingClick} primary>
      Simulate loading
    </Button>
    <Divider />

    <CardGroup doubling itemsPerRow={3} stackable>
      {_.map(cards, (card) => (
        <Card key={card.header}>
          {loading ? (
            <Placeholder>
              <PlaceholderImage square />
            </Placeholder>
          ) : (
            <Image src={card.avatar} style={{ height: 70, width: 70 }} />
          )}

          <CardContent>
            {loading ? (
              <Placeholder>
                <PlaceholderHeader>
                  <PlaceholderLine length='very short' />
                  <PlaceholderLine length='medium' />
                </PlaceholderHeader>
                <PlaceholderParagraph>
                  <PlaceholderLine length='short' />
                </PlaceholderParagraph>
              </Placeholder>
            ) : (
              <>
                <CardHeader>{card.header}</CardHeader>
                <CardHeader>{card.header2}</CardHeader>
                <CardMeta>{card.date}</CardMeta>
                <CardDescription>{card.description}</CardDescription>
              </>
            )}
          </CardContent>
          <CardContent extra>
            <Button disabled={loading} primary>
              Vote
            </Button>
          </CardContent>
        </Card>
      ))}
    </CardGroup>
  </>
};

render(<VotingUI />, document.querySelector('#root'));

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
